CREATE TABLE `dnd5e_background` (
  `background` varchar(15) NOT NULL,
  `name` varchar(10) NOT NULL,
  `description` text NOT NULL,
  `skill` text DEFAULT NULL,
  `language` text DEFAULT NULL,
