--
-- 資料表結構 `dnd5e_subclasses`
--

CREATE TABLE `dnd5e_subclasses` (
  `subclass` varchar(20) NOT NULL,
  `name` varchar(10) NOT NULL,
  `parent_class` varchar(20) NOT NULL,
  `description` text NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- 傾印資料表的資料 `dnd5e_subclasses`
--

INSERT INTO `dnd5e_subclasses` (`subclass`, `name`, `parent_class`, `description`) VALUES
('champion', '勇士', 'fighter', '勇士範型專注於發展純粹的身體能力，將其磨練到完美。那些仿效於此範型的人們結合嚴格的訓練和卓越的身體素質以造成最致命的打擊。'),
('arcane_archer', '魔射手', 'fighter', '魔射手鑽研著那將魔法編入攻擊以產生超自然效果的獨特精靈箭技。魔射手是精靈中最精銳的戰士。他們會駐足守望著精靈領域的疆界，銳眼緊盯著入侵者，並射出灌注魔法的箭矢以在怪物和入侵者闖入精靈的居住地之前擊潰它們。在幾個世紀以來，這種精靈箭技也已經被其他能夠在祕法才能和弓道中找到平衡的種族所習得。'),
('berserker', '狂戰士', 'barbarian', '對某些野蠻人來說，狂暴是達到極致的一種方式，這種極致即是暴力。狂戰士道途是追求鮮血與無拘的狂怒的道路。當你進入狂戰士的狂暴時，你在戰斗的混沌快感中戰栗，舍生忘死般的沖殺。');

-- --------------------------------------------------------

--
-- 資料表結構 `dnd5e_sublist_choice`
--

CREATE TABLE `dnd5e_sublist_choice` (
  `id` int(11) NOT NULL,
  `title` varchar(20) NOT NULL,
  `description` text NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- 傾印資料表的資料 `dnd5e_sublist_choice`
--

INSERT INTO `dnd5e_sublist_choice` (`id`, `title`, `description`) VALUES
(1, '守護', '當一個你所能看見的生物攻擊一個距離你5呎內你以外的目標，你可以使用你的反應以讓該攻擊檢定承受劣勢。你必須持握一面盾牌。'),
(2, '巨武戰鬥', '當你在使用一把以雙手持用的近戰武器所進行攻擊的傷害骰中骰出了1或2的結果，你可以重骰該骰，且必須使用新的擲骰結果，即使其結果仍然是1或2。該武器必須具有雙手或可雙手屬性才能讓你獲得這個好處。'),
(3, '弓道', '你在使用遠程武器進行的攻擊檢定中獲得+2加值。'),
(4, '決鬥', '當你單手持用一把近戰武器，且沒有持用其他武器時，你在使用該武器所進行的傷害骰中獲得+2加值。'),
(5, '防禦', '當你穿戴護甲時，你的AC獲得+1加值。'),
(6, '雙武器戰鬥', '當你進行雙武器戰鬥時，你可以將你的屬性調整值加入第二次攻擊的傷害中。'),
(7, '盲斗', '你獲得10尺盲視。在10尺範圍內，你可以看見除了處於全遮掩后的任何東西，哪怕你正在被目盲或是在黑暗之中。另外，你可以看見該範圍內的未對你成功躲藏的隱形生物。'),
(8, '攔截', '當一個你可見的生物攻擊距離你5尺以內你以外的目標時，你能以一個反應將目標受到的傷害減少1d10+你熟練加值（最多減到0傷害）。當你手持一面盾牌或者一把簡單或軍用武器時才能執行此反應。'),
(9, '卓越技巧', '你從戰鬥大師武術范型的戰技列表中習得一項戰技。如果該戰技需要豁免，則豁免DC為8+你的熟練加值+你的力量或敏捷調整值（由你選擇）。你獲得一個d6的卓越骰（此卓越骰與任何你能夠其他方式獲得的卓越骰累計）。此卓越骰可用於啟動你的戰技。卓越骰一經使用即消耗，並在完成一次短休或長休后恢復。'),
(10, '投擲武器戰鬥', '你可以在使用一件帶有投擲屬性武器進行進攻時，將那件武器作為攻擊的一部分而拔出。另外，在你以一件投擲武器于遠程攻擊中命中對手時，你的傷害擲骰+2。'),
(11, '徒手戰鬥', '你的徒手打擊可以造成1d6+你力量調整值的鈍擊傷害，如果你沒有手持任何武器或盾牌進行攻擊，傷害骰從d6變成d8。每個你的回合開始時，你可以對被你擒抱的生物造成1d4鈍擊傷害。'),
(12, '放逐箭', '你使用防護系魔法以嘗試暫時地將你的目標放逐到一個位於妖精荒野的無害區域。被此箭命中的生物必須成功通過一次魅力豁免，否則將被放逐。當因為這個方式被放逐，目標的移動速度將歸零，且陷入無力狀態。在它的下個回合結束時，目標將重新出現在它離開時所在的空間，或若該空間已被佔據則出現在最近一處未被佔據空間。\n\r在你本職業升至18級之後，當被此箭命中時，目標同時也將受到2d6力場傷害。'),
(13, '暗影箭', '你將幻術系魔法編入你的箭矢中，使它以暗影遮蔽你敵人的視線。被此箭命中的生物將受到額外的2d6精神傷害，且它必須成功通過一次睿知豁免，否則直到你的下個回合開始前都無法看見任何5呎以外的事物。\n\r這個精神傷害將在你本職業升至18級時增加至4d6。'),
(14, '爆裂箭', '你將汲取自塑能學派的力場能量注入你的箭矢。這股能量將在你攻擊之後爆發。緊接在此箭命中一個生物之後，目標和所有距離它10呎內的其他生物都將各自受到2d6力場傷害。\n\r這個力場傷害將在你本職業升至18級時增加至4d6。'),
(15, '穿透箭', '你利用變化系魔法以將乙太屬性賦予你的箭矢。當你使用此選項時，你不需要為此攻擊進行攻擊檢定。取而代之，這支箭矢將在消失前向前射出一道1呎寬、30呎長的直線。這支箭矢將無害地穿過物體、忽略掩護。每個在直線內的生物都必須進行一次敏捷豁免。若豁免失敗，該生物將受到如同被箭矢命中一樣傷害，再加上額外的1d6穿刺傷害。若豁免成功，則目標只受到一半的傷害。\n\r這個穿刺傷害將在你本職業升至18級時增加至2d6。'),
(16, '糾纏箭', '當此箭擊中它的目標，咒法系魔法將創造出一團糾纏著的有毒刺藤，緊緊纏繞著目標。被此箭命中的生物將受到額外的2d6毒素傷害，它的移動速度降低10呎，且它在每回合不透過傳送而第一次移動1呎或以上的距離時，將再受到2d6劈砍傷害。目標或其他能夠觸及它的生物可以使用它的動作，並藉由成功通過一次對抗你祕法射擊豁免DC的力量（運動）檢定以移除這些刺藤。否則，這些刺藤將持續存在長達1分鐘，或直到你再次使用這個選項。\n\r這個毒素傷害和劈砍傷害將在你本職業升至18級時增加至4d6。'),
(17, '衰弱箭', '你將死靈系魔法編入你的箭矢中。被此箭命中的生物將受到額外的2d6死靈傷害。目標也必須成功通過一次體質豁免，否則它武器攻擊所造成的傷害將被減半，持續直到你的下個回合開始。\n\r這個死靈傷害將在你本職業升至18級時增加至4d6。'),
(18, '迷惑箭', '你的惑控系魔法讓這支箭能暫時地迷惑它的目標。被此箭命中的生物將受到額外的2d6精神傷害，且你選擇其中一個距離目標30呎內的你的盟友。目標必須成功通過一次睿知豁免，否則被所選的目標魅惑直到你的下個回合開始。這個效果會在所選的盟友攻擊被魅惑目標、對目標造成傷害、或強迫它進行豁免檢定時提早結束。\n\r這個精神傷害將在你本職業升至18級時增加至4d6。'),
(19, '追蹤箭', '利用預言系魔法，你賦予你的箭矢追蹤目標的能力。當你使用此選項時，你不需要為此攻擊進行攻擊檢定。取而代之，選擇一個你在過去1分鐘內曾見過的生物。這支箭矢將飛向該生物，若有需要的話則繞過轉角，並忽略四分之三掩護和半掩護。若該目標在此武器的射程內，且其存在一條能讓箭矢通往目標的途徑，則目標必須進行一次敏捷豁免。否則這支箭矢將在飛至最遠距離之後消失。若豁免失敗，目標將受到如同被箭矢命中一樣傷害，再加上額外的1d6力場傷害，且你將得知目標當下所在的位置。若豁免成功，則目標只受到一半的傷害，且你無法得知它所在的位置。\n\r這個力場傷害將在你本職業升至18級時增加至2d6。');

-- --------------------------------------------------------

--
-- 資料表結構 `dnd5e_subraces`
--

CREATE TABLE `dnd5e_subraces` (
  `subrace` varchar(20) NOT NULL,
  `name` varchar(10) NOT NULL,
  `parent_race` varchar(20) NOT NULL,
  `description` text NOT NULL,
  `speed` text DEFAULT NULL,
  `darkvision` text DEFAULT NULL,
  `prof` text DEFAULT NULL,
  `spell` text DEFAULT NULL,
  `ability` text DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- 傾印資料表的資料 `dnd5e_subraces`
--

INSERT INTO `dnd5e_subraces` (`subrace`, `name`, `parent_race`, `description`, `speed`, `darkvision`, `prof`, `spell`, `ability`) VALUES
('seaelf', '海精靈', 'elf', '在最早的時期，海精靈就深深愛上了海洋那狂野之美。當其他精靈們在一個個國度之間旅行，海精靈則航行於最深的洋流，探索著百千世界的水域。如今，他們大多生活在海岸淺灘或水元素位面中，組成了小型而隱匿的聚落。', '{\"swim\": \"30\"}', NULL, '{\"weapon\": \"矛|三叉戟|輕弩|捕網\",\"language\": \"水族\"}', NULL, NULL),
('woodelf', '木精靈', 'elf', '做為一名木精靈，你具有敏銳的感官和直覺，且你迅捷的步伐讓你得以快速而隱密的穿梭在你熟悉的森林中。這個分類包括了灰鷹世界的野精靈(wild elf)（又被稱作革魯哈祈(grugach)）、龍槍世界的卡貢納斯提精靈(Kagonesti)、以及在灰鷹世界和被遺忘國度中被稱作木精靈(wood elf)的種族。在費倫，木精靈（又被稱作野精靈、綠精靈、或森精靈）離群索居，且並不信任非精靈種族。\\n\\r木精靈的肌膚傾向赤銅色調，有時會帶有點蒼綠。他們的頭髮偏向棕色和黑色，但偶爾也會出現金黃色或赤銅色系。他們的眼睛呈現綠色、棕色、或淡褐色。', '{\"walk\": \"35\"}', NULL, '{\"weapon\": \"長劍|短劍|短弓|長弓\"}', NULL, NULL),
('highelf', '高等精靈', 'elf', '做為一名高等精靈，你具有敏銳的心靈且精通至少一種基礎魔法。在D&D的許多世界中，存在著二種高等精靈。其中一種（包括了灰鷹世界的灰精靈(gray elf)和山谷精靈(valley elf)、龍槍世界的西瓦納斯提精靈(Silvanesti)、和被遺忘國度的日精靈(sun elf)）傲慢而孤僻，深信他們自己遠比非精靈或甚至其他精靈種族來的更加優越。另一種類型（包括灰鷹世界的高等精靈(high elf)、龍槍世界的奎靈納斯提精靈(Qualinesti)、和被遺忘國度的月精靈(moon elf)）則更常見且更為友善，並且經常能在人類和其他種族之中被見到。', NULL, NULL, '{\"weapon\": \"長劍|短劍|短弓|長弓\", \"language\": \"any\"}', NULL, NULL),
('drowelf', '暗精靈', 'elf', '傳承自一個早期精靈的暗色皮膚亞種，卓爾因為追隨女神羅絲(Lolth)踏上邪惡與腐化之路，而被從地表世界放逐。如今，他們已經在幽暗地域的深處建立起了自己的文明，遵循著羅絲女神的意志。卓爾也被稱作黑暗精靈，他們有著如拋光黑曜石般的黑色肌膚，以及蒼白或淡白色的秀髮。他們通常有著帶淡紫、銀白、粉紅、或藍色色調的淡色瞳孔（以至於常被誤認為白色）。他們的身材比大部分精靈要來得更嬌小而細瘦。', NULL, NULL, '{\"weapon\": \"刺劍|短劍|手弩\"}', NULL, NULL),
('hilldwarf', '丘陵矮人', 'dwarf', '作為丘陵矮人，你有敏銳的觸覺、深邃的直覺、和可觀\r\n的適應力。費倫的富強南國的金矮人 gold dwarf 屬於丘\r\n陵矮人，而龍槍世設的克莱恩中被逐離家園奈逹氏族\r\nNeidar 和流離失所的克拉氏族 Klar 亦然。', NULL, NULL, NULL, NULL, NULL),
('mountaindwarf', '高山矮人', 'dwarf', '作為高山矮人，你身強力壯，習慣了崎嶇地形裡的艱難\r\n生活。以矮人而言你應該偏向高大和淡膚色。住在費倫\r\n北地的盾矮人 shield dwarf，以及龍槍中統治矮人的海\r\n勒氏族 Hylar 和貴族階級的逹瓦氏族 Daewar 都是高山\r\n矮人。', NULL, NULL, '{\"armor\": \"輕甲|中甲\"}', NULL, '{\"str\": \"+1\"}'),
('duergardwarf', '灰矮人', 'dwarf', '灰矮人們認為勤勞的價值高於一切。在他們的文化中顯露除了冷酷的決心或者是憤怒以外的感情是絕不可取的，不過他們看起來會為勞作而感到愉快。他們能像典型矮人那般欣賞規矩的、傳統的、完美的工藝，不過他們只用排除了美學和藝術價值的絕對功利性來衡量自己的商品。\r\n很少有灰矮人會成為冒險者，而他們當中出現在地表世界的更少因為他們是保守而多疑。通常只有被流放者才會願意離開他們自己的地下都市。請與你的地下城主討論你是否能扮演一名灰矮人角色。', NULL, NULL, '', '{\"灰矮人魔法\":{\"spells\":{\"3\": {\"變巨/縮小術\":{\"time\":\"1/日\", \"remark\":\"只能使用變巨的選項。\"}},\"5\": {\"隱形術\":{\"time\":\"1/日\", \"remark\":\"\"}}},\"remark\": \"\",\"recover\": \"LR\"}}', NULL);

